import http from '@core/services/http';
import { buildQueryString } from '@core/utils/url';
import { RESPONSE_TYPE_BLOB } from '@core/constants/http';
export default {
  createTicket: function createTicket(payload) {
    return http.post('/tickets', payload);
  },
  fetchTicket: function fetchTicket(id) {
    return http.get("/tickets/".concat(id));
  },
  fetchTickets: function fetchTickets(params) {
    return http.get("/tickets?".concat(buildQueryString(params)));
  },
  countTickets: function countTickets(params) {
    return http.get("/tickets/count?".concat(buildQueryString(params)));
  },
  pushMessage: function pushMessage(payload) {
    return http.post("/tickets/".concat(payload.ticket_id, "/message"), payload);
  },
