    return http.get("/tickets/".concat(id, "/messages/count?").concat(buildQueryString(params)));
  },
  deleteMessage: function deleteMessage(ticket_id, id) {
    return http.delete("/tickets/".concat(ticket_id, "/message/").concat(id));
  },
  forwardTicket: function forwardTicket(payload) {
    return http.post("/tickets/".concat(payload.ticket_id, "/forward"), payload);
  },
  fetchStatisticTicketStatus: function fetchStatisticTicketStatus(payload) {
    return http.get("/tickets/statistic/status?".concat(buildQueryString(payload)));
  },
