export default {
  name: 'ModalImport',
  props: {
    visible: {
      type: Boolean
    },
    title: {
      type: String,
      default: ''
    },
    supportUrl: {
      type: String,
      default: ''
    },
    uploading: {
      type: Boolean,
      default: false
    },
    accept: {
      type: [Object, Array, String],
      default: ''
    },
    error: {
      type: String,
      default: ''
    }
  },
  data: function data() {
    return {
      isSubmit: false,
      file: null,
      isVisible: false,
      isValid: true
    };
  },
  methods: {
    handleClose: function handleClose() {
      this.$emit('update:visible', false);
    },
    handleSave: function handleSave() {
      if (this.uploading) {
        console.log('selected');
        return;
      }

      this.$emit('selected', this.file);
    },
    handleFileChange: function handleFileChange(e) {
      var files = e.target.files || e.dataTransfer.files;

      if (files.length) {
        this.file = files[0];
      }
    }
  },
  watch: {
    file: {
      handler: function handler(val) {
        this.file = val;
      },
      immediate: true
    },
    visible: {
      handler: function handler(val) {
        this.isVisible = val;
      },
      immediate: true
    }
  }
};
