import { isObject } from '@core/utils/type';
export default {
  name: 'Sidebar',
  props: {
    isSidebarOpen: {
      type: Boolean,
      default: true
    }
  },
  computed: {
    menus: function menus() {
      return [{
        title: 'Home',
        icon: 'dashboard',
        route: '/'
      }, {
        title: 'Create video',
        icon: 'image',
        route: {
          name: 'create-video'
        }
      }, {
        title: 'My video',
        icon: 'report',
        route: {
          name: 'order-list'
        }
      }, {
        title: 'Pricing',
        icon: 'payment',
        route: {
