      } // TODO: uncomment me
      // {
      //   title: 'Setting',
      //   icon: 'settings',
      //   route: { name: 'settings' },
      // },
      ];
    },
    availableMenus: function availableMenus() {
      return this.menus.filter(function (menu) {
        return menu.disable !== true;
      });
    }
  },
  data: function data() {
    return {
      hoverIndex: -1
    };
  },
  methods: {
    isActive: function isActive(route) {
      if (isObject(route)) {
        return this.$route.name === route.name;
      }

      return this.$route.path === route || this.$route.fullPath === route;
    },
    onHover: function onHover(i) {
      this.hoverIndex = i;
    }
  }
};
