var Browser = {
  supportLocalStorage: null,

  /**
   * Return true if this browser support localStorage
   * @returns {*}
   */
  isSupportLocalStorage: function isSupportLocalStorage() {
    if (this.supportLocalStorage === null) {
      var test = 'test';

      try {
        localStorage.setItem(test, test);
        localStorage.removeItem(test);
        this.supportLocalStorage = true;
      } catch (e) {
        this.supportLocalStorage = false;
      }
    }

    return this.supportLocalStorage;
  },

  /**
   * Redirect to url
   * @param url
   */
  redirect: function redirect(url) {
    window.location = url;
  },

  /**
   * Open _new tab
   * @param url
   */
  openNewTab: function openNewTab(url) {
    var win = window.open(url, '_blank');

    if (!win) {
      window.location.href = url;
    }
  },

  /**
   * Insert param to url
   * @param myUrl
   * @param name
   * @param value
   * @returns {*}
   */
  insertParamToUrl: function insertParamToUrl(myUrl, name, value) {
    var newUrl = myUrl;
    var re = new RegExp("([?&]".concat(name, "=)[^&]+"), '');

    function add(sep) {
      newUrl += "".concat(sep).concat(name, "=").concat(encodeURIComponent(value));
    }

    function change() {
      newUrl = newUrl.replace(re, "$1".concat(encodeURIComponent(value)));
    }

    if (newUrl.indexOf('?') === -1) {
      add('?');
    } else if (re.test(newUrl)) {
      change();
    } else {
      add('&');
    }

    return newUrl;
  },

  /**
   * Insert params to url
   * @param myUrl
   * @param params
   * @returns {*}
   */
  insertParamsToUrl: function insertParamsToUrl(myUrl, params) {
    var _this = this;

    var newUrl = myUrl;
    Object.keys(params).forEach(function (k) {
      if (typeof params[k] !== 'undefined') {
        newUrl = _this.insertParamToUrl(newUrl, k, params[k]);
      }
    });
    return newUrl;
  },

  /**
   * Get Url Param
   * @param name
   * @returns {string|null}
   */
  getURLParameter: function getURLParameter(name) {
    return decodeURIComponent((new RegExp("[?|&]".concat(name, "=([^&;]+?)(&|#|;|$)")).exec(window.location.search) || [null, ''])[1].replace(/\+/g, '%20')) || null;
  },

  /**
   * Download by link
   * @param link
   * @param filename
   */
  download: function download(link) {
    var filename = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 'download';
    var element = document.createElement('a');
    element.setAttribute('href', link);
    element.setAttribute('download', filename);
    element.style.display = 'none';
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  },

  /**
   * Download blob
   * @param blob
   * @param filename
   */
  downloadBlob: function downloadBlob(blob) {
    var filename = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 'download';
    var url = window.URL.createObjectURL(blob);
    this.download(url, filename);
  },
  base64ToArrayBuffer: function base64ToArrayBuffer(base64) {
    var binaryString = window.atob(base64);
    var binaryLen = binaryString.length;
    var bytes = new Uint8Array(binaryLen);

    for (var i = 0; i < binaryLen; i++) {
      bytes[i] = binaryString.charCodeAt(i);
    }

    return bytes;
  },
  saveByteArray: function saveByteArray(fileName, byte, type) {
    var blob = new Blob([byte], {
      type: type
    });
    var link = document.createElement('a');
    link.href = window.URL.createObjectURL(blob);
    link.download = fileName;
    link.click();
  }
};
