import http from '@core/services/http';
import { buildQueryString } from '@core/utils/url';
export default {
  /**
   * Fetch designs
   * @param payload
   * @return {*}
   */
  fetchDesigns: function fetchDesigns(payload) {
    return http.get("/designs?".concat(buildQueryString(payload)));
  },

  /**
   * Fetch designs
   * @param designID
   * @return {*}
   */
  fetchDesignFiles: function fetchDesignFiles(designID) {
    return http.get("/designs/".concat(designID, "/files"));
  },

  /**
   * Fetch design count
   * @param payload
   * @return {*}
   */
  fetchDesignCount: function fetchDesignCount(payload) {
    return http.get("/designs/count?".concat(buildQueryString(payload)));
  },

  /**
   * Fetch design
   * @param designId
   * @return {*}
   */
  fetchDesign: function fetchDesign(designId) {
    return http.get("/designs/".concat(designId));
  },

  /**
   * Create design
   * @param payload
   * @return {*}
   */
  create: function create(payload) {
    return http.post("/designs", payload);
  },

  /**
   * Update design
   * @param payload
   * @return {*}
   */
  update: function update(payload) {
    return http.put("/designs/".concat(payload.id, "/files"), payload.body);
  },
  updateSku: function updateSku(payload) {
    return http.put("/designs/".concat(payload.id), payload);
  },
  delete: function _delete(payload) {
    return http.delete("/designs/".concat(payload.id));
  },
  deleteDesignFile: function deleteDesignFile(payload) {
    return http.delete("/designs/".concat(payload.design_id, "/files/").concat(payload.id));
  },

  /**
   * Update design
   * @param payload
   * @return {*}
   */
  archive: function archive(payload) {
    return http.put("/designs/archive", payload);
  },

  /**
   * Missing thumbnail
   * @param {String} url
   * @return {*}
   */
  missingThumbnail: function missingThumbnail(url) {
    return http.get("/designs/miss-thumbnail?".concat(buildQueryString({
      url: url
    })));
  },
  getImage: function getImage(url) {
    return http.get(url, {
      type: 'blob'
    });
  }
};
