import http from '@core/services/http';
import { buildQueryString } from '@core/utils/url';
export default {
  /**
   * Fetch container
   * @param payload
   * @return {*}
   */
  fetchContainers: function fetchContainers(payload) {
    return http.get("/orders/containers?".concat(buildQueryString(payload)));
  },

  /**
   * Fetch order count
   * @param payload
   * @return {*}
   */
  fetchContainerCount: function fetchContainerCount(payload) {
    return http.get("/orders/containers/count?".concat(buildQueryString(payload)));
  },

  /**
   * Fetch container
   * @param id
   * @return {*}
   */
  fetchContainer: function fetchContainer(id) {
    return http.get("/orders/containers/".concat(id));
  },

  /**
   * Fetch orders
   * @param payload
   * @return {*}
   */
  fetchOrders: function fetchOrders(payload) {
    return http.get("/orders?".concat(buildQueryString(payload)));
  },

  /**
   * Fetch order count
   * @param payload
   * @return {*}
   */
  fetchOrderCount: function fetchOrderCount(payload) {
    return http.get("/orders/count?".concat(buildQueryString(payload)));
  },

  /**
   * Fetch order
   * @param payload
   * @return {*}
   */
  fetchOrder: function fetchOrder(payload) {
    return http.get("/orders/".concat(payload));
  },

  /**
   * Import orders
   * @param payload
   * @return {*}
   */
  importOrders: function importOrders(payload) {
    var formData = new FormData();
    formData.append('file', payload.file);

    if (payload.shop_id) {
      formData.append('shop_id', payload.shop_id);
    }

    return http.post('/orders/import', formData);
  },

  /**
   * Preview import orders
   * @param payload
   * @return {*}
   */
  previewImportOrders: function previewImportOrders(payload) {
    var formData = new FormData();
    formData.append('file', payload.file);

    if (payload.shop_id) {
      formData.append('shop_id', payload.shop_id);
    }

    return http.post('/orders/import/preview', formData);
  },

  /**
   * Update order
   * @param payload
   * @return {*}
   */
  updateOrder: function updateOrder(payload) {
    return http.put("/orders/".concat(payload.id), {
      order: payload
    });
  },

  /**
   * Create container
   * @param payload
   * @return {*}
   */
  createContainer: function createContainer(payload) {
    return http.post('/orders/containers', payload);
  },

  /**
   * Append orders to container
   * @param payload
   * @return {*}
   */
  appendContainer: function appendContainer(payload) {
    return http.put('/orders/containers/append', payload);
  },

  /**
   * Create container
   * @param payload
   * @return {*}
   */
  updateContainer: function updateContainer(payload) {
    return http.put("/orders/containers/".concat(payload.id), {
      container: payload
    });
  },

  /**
   * Export container
   * @param id
   * @return {*}
   */
  exportContainer: function exportContainer(id) {
    return http.post('/orders/export', {
      container_id: id
    });
  },

  /**
   * Export orders
   * @param ids
   * @return {*}
   */
  exportOrders: function exportOrders(ids) {
    return http.post('/orders/export', {
      order_ids: ids
    });
  },

  /**
   * Get Logs
   * @param {String} url
   * @return {*}
   */
  getLogs: function getLogs(params) {
    return http.get("/logs?".concat(buildQueryString(params)));
  }
};
