export default {
  name: 'App',
  data: function data() {
    return {
      isShowScrollTop: false
    };
  },
  metaInfo: function metaInfo() {
    return {
      title: this.$route.meta.title || '',
      titleTemplate: '%s - Lionnix'
    };
  },
  created: function created() {
    var _this = this;

    window.onscroll = function () {
      var pos = window.pageYOffset;

      if (pos > window.innerHeight) {
        _this.isShowScrollTop = true;
      } else {
        _this.isShowScrollTop = false;
      }
    };
  },
  computed: {
    layout: function layout() {
      return "".concat(this.$route.meta.layout || 'default', "-layout");
    }
  },
  components: {
    'default-layout': function defaultLayout() {
      return import('@layouts/v2/Index');
    },
    'full-page-layout': function fullPageLayout() {
      return import('@layouts/v2-full-page/Index');
    }
  },
  methods: {
    scrollToTop: function scrollToTop() {
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    }
  }
};
