export default {
  name: 'PProgress',
  props: {
    type: {
      type: [String, Object],
      default: 'dark',
    },
    size: String,
    value: {
      type: Number,
      default: undefined,
    },
    max: {
      type: Number,
      default: 100,
    },
    showValue: {
      type: Boolean,
      default: false,
    },
    format: {
      type: String,
      default: 'raw',
      validator: (value) => {
        return ['raw', 'percent'].indexOf(value) >= 0
      },
    },
    precision: {
      type: Number,
      default: 2,
    },
    keepTrailingZeroes: {
      type: Boolean,
      default: false,
    },
  },
  computed: {
    isIndeterminate() {
      return this.value === undefined || this.value === null
    },
    newType() {
      return [
        this.size ? `is-${this.size}` : '',
        this.type ? `is-${this.type}` : '',
      ]
    },
    newValue() {
      if (
        this.value === undefined ||
        this.value === null ||
        isNaN(this.value)
      ) {
        return undefined
      }

      if (this.format === 'percent') {
        const val = this.toFixed((this.value * this.max) / 100)
        return `${val}%`
      }
      const val = this.toFixed(this.value)
      return val
    },
  },
  watch: {
    value(value) {
      this.setValue(value)
    },
  },
  methods: {
    /**
     * When value is changed back to undefined, value of native progress get reset to 0.
     * Need to add and remove the value attribute to have the indeterminate or not.
     */
    setValue(value) {
      if (this.isIndeterminate) {
        this.$refs.progress.removeAttribute('value')
      } else {
        this.$refs.progress.setAttribute('value', value)
      }
    },
    // Custom function that imitate the javascript toFixed method with improved rounding
    toFixed(num) {
      let fixed = (+`${Math.round(+`${num}e${this.precision}`)}e${-this
        .precision}`).toFixed(this.precision)
      if (!this.keepTrailingZeroes) {
        fixed = fixed.replace(/\.?0+$/, '')
      }
      return fixed
    },
  },
  mounted() {
    this.setValue(this.value)
  },
}
