import { date } from '@core/utils/datetime'

export default {
  name: 'ModalTicketFilter',
  props: {
    categories: {
      type: Array,
      default: () => [],
    },
    src: {
      type: String,
      default: '',
    },
    visible: {
      type: Boolean,
      default: false,
    },
  },
  data() {
    return {
      isShow: this.visible,
      cb_accountant: [],
      filter: {
        category: '',
        start_date: '',
        end_date: '',
        seller: 0,
        support: 0,
        accountant: '',
      },
    }
  },
  methods: {
    handleClose() {
      this.$emit('update:visible', false)
    },
    handleFilter() {
      this.$emit('update', this.filter)
    },
    selectDate(v) {
      this.filter.start_date = date(v.startDate, 'yyyy-MM-dd')
      this.filter.end_date = date(v.endDate, 'yyyy-MM-dd')
    },
  },
  watch: {
    visible(value) {
      this.isShow = value
    },
  },
}
