import { capitalize } from '@core/utils/string'

export default {
  name: 'StatusTab',
  props: {
    status: {
      type: Array,
      default: () => [],
    },
    value: {
      type: String,
      default: '',
    },
    hasAll: {
      type: Boolean,
      default: true,
    },
  },
  computed: {
    cleanStatus() {
      const status = this.hasAll ? [{ value: '', text: 'All' }] : []

      this.status.forEach((item) => {
        status.push({ value: item, text: capitalize(item) })
      })

      return status
    },
  },
  methods: {
    handleClick(item) {
      if (this.value !== item) {
        this.$emit('input', item)
        this.$emit('click', item)
      }
    },
  },
}
