import Thumbnail from '@components/shared/Thumbnail'
import ModalChangeDesign from './ModalChangeDesign'
export default {
  name: 'OrdersItems',
  components: {
    Thumbnail,
    ModalChangeDesign,
  },
  props: {
    display: {
      type: String,
      default: 'order',
    },
    items: {
      type: Array,
      default: () => [],
    },
    orderId: {
      type: Number,
      default: 0,
    },
    editing: {
      type: Boolean,
      default: false,
    },
    fetchDesigns: {
      type: Function,
      default: () => {},
    },
    productTypes: {
      type: Array,
      default: () => [],
    },
    designs: {
      type: Array,
      default: () => [],
    },
    shopId: {
      type: Number,
      default: 0,
    },
    isAllowUpdate: {
      type: Boolean,
      default: false,
    },
    order: {
      type: Object,
      default: () => {},
    },
  },
  data() {
    return {
      isVisible: false,
      item: {},
    }
  },
  computed: {
    imagePlaceholder() {
      return require('@assets/img/image-placeholder.png')
    },
  },
  methods: {
    handleChangeDesign(item) {
      if (!this.isAllowUpdate) return
      this.item = item
      this.isVisible = true
    },

    handleSelectedDesign({ url, design }) {
      this.$emit('selectedDesign', {
        design,
        url,
        item: this.item,
        orderId: this.orderId,
      })
      this.isVisible = false
    },

    getAttributes(attributes) {
      const attribute = []

      if (attributes) {
        Object.keys(attributes).forEach((key) => {
          attribute.push(`${key}: ${attributes[key]}`)
        })
      }

      return attribute.join(' | ')
    },

    getPrice(item) {
      if (typeof item.product_type !== 'undefined' && item.product_type.price) {
        return item.product_type.price
      }

      return item.price || 0
    },
  },
}
