import { mapState, mapActions } from 'vuex'
import EmptySearchResult from '@components/shared/EmptySearchResult'
import debounce from 'lodash/debounce'
import mixinRoute from '@core/mixins/route'
import mixinTable from '@core/mixins/table'
import { VclTable } from 'vue-content-loading'
import ModalFilter from './components/ModalFilter'
import { truncate } from '@core/utils/string'

export default {
  name: 'TicketList',
  mixins: [mixinRoute, mixinTable],
  components: {
    EmptySearchResult,
    VclTable,
    ModalFilter,
  },
  data() {
    return {
      showModalFilter: false,
      isFetching: false,
      result: {},
      filterKeyShow: [
        'support',
        'search',
        'category',
        'seller',
        'accountant',
        'start_date',
        'end_date',
        'sort',
      ],
      filter: {
        page: 1,
        search: '',
        sort: '',
        limit: 50,
      },
    }
  },
  filters: {
    textKeyFilter(v) {
      return v.replace('_', ' ')
    },
  },
  computed: {
    ...mapState('ticket', {
      tickets: (state) => state.tickets,
      count: (state) => state.count,
      categories: (state) => state.categories,
    }),

    ticketStatus() {
      return ['open', 'process', 'close']
    },
  },
  created() {
    this.filter = this.getRouteQuery()
  },
  methods: {
    truncate,
    ...mapActions('ticket', ['fetchTickets', 'fetchCategories']),

    async init() {
      this.isFetching = true
      this.handleUpdateRouteQuery()
      this.result = await this.fetchTickets(this.filter)
      await this.fetchCategories()
      this.isFetching = false
    },

    handleSearch: debounce(function(e) {
      this.filter.search = e.target.value
    }, 500),

    handlerCreateTicket() {
      this.$router.push({ name: 'ticket-form' })
    },

    onSort(order) {
      this.filter.sort = order
    },
    handlerShowModalFilter() {
      this.showModalFilter = true
    },

    handlerFilterAdvanced(v) {
      this.filter = Object.assign(this.filter, v)
      this.init()
      this.showModalFilter = false
    },
  },
  watch: {
    filter: {
      handler: function() {
        this.init()
      },
      deep: true,
    },
  },
}
