import PIcon from '../icon/Icon'
const TYPE_CLASSES_MAP = {
  success: 'check',
  warning: 'alert',
  error: 'close',
  info: 'info',
}

export default {
  name: 'PAlert',
  components: {
    PIcon,
  },
  props: {
    title: {
      type: String,
      default: '',
    },
    description: {
      type: String,
      default: '',
    },
    type: {
      type: String,
      default: 'info',
    },
    closable: {
      type: Boolean,
      default: true,
    },
    closeText: {
      type: String,
      default: '',
    },
    showIcon: Boolean,
    center: Boolean,
    effect: {
      type: String,
      default: 'light',
      validator: function(value) {
        return ['light', 'dark'].indexOf(value) !== -1
      },
    },
  },

  data() {
    return {
      visible: true,
    }
  },

  methods: {
    close() {
      this.visible = false
      this.$emit('close')
    },
  },

  computed: {
    wrapperClasses() {
      return [
        `p-alert`,
        `p-alert--${this.type}`,
        `is-${this.effect}`,
        this.center ? 'is-center' : '',
      ]
    },
    typeClass() {
      return `p-alert--${this.type}`
    },

    iconName() {
      return TYPE_CLASSES_MAP[this.type]
    },

    isBigIcon() {
      return this.description || this.$slots.default ? 'is-big' : ''
    },

    isBoldTitle() {
      return this.description || this.$slots.default ? 'is-bold' : ''
    },
  },
}
