import { mapActions } from 'vuex'
import VueRecaptcha from 'vue-recaptcha'

export default {
  name: 'forgot',
  components: { VueRecaptcha },
  data() {
    return {
      email: null,
      form: {
        checkCaptcha: false,
      },
      requiredEmail: false,
      check: true,
    }
  },
  methods: {
    ...mapActions('auth', ['forgotPassword']),
    checkRequired() {
      let result = true
      if (!this.email) {
        this.requiredEmail = true
        result = false
      } else {
        this.requiredEmail = false
      }
      return result
    },

    async onForgot() {
      if (!this.checkRequired()) {
        return
      }
      if (!this.form.checkCaptcha) {
        this.check = false
        return
      }
      const params = {
        email: this.email,
      }
      const response = await this.forgotPassword(params)
      if (response.success) {
        this.$refs.recapcha.reset()
        this.form.checkCaptcha = false
        this.$toast.open({
          type: 'success',
          message: response.message,
        })
      } else {
        this.$refs.recapcha.reset()
        this.form.checkCaptcha = false
        this.$toast.open({
          type: 'error',
          message: response.error,
        })
      }
    },
    onVerify: function(response) {
      if (response) this.form.checkCaptcha = true
      this.check = true
    },
    onCaptchaExpired: function() {
      this.form.checkCaptcha = false
    },
  },
  computed: {
    recapchaKey() {
      return `${process.env.VUE_APP_RECAPCHA_KEY}`
    },
  },
}
