import { isObject } from '@core/utils/type'

export default {
  name: 'Sidebar',
  props: {
    isSidebarOpen: {
      type: Boolean,
      default: true,
    },
  },
  computed: {
    menus() {
      return [
        {
          title: 'Home',
          icon: 'dashboard',
          route: '/',
        },
        {
          title: 'Create video',
          icon: 'image',
          route: { name: 'create-video' },
        },
        {
          title: 'My video',
          icon: 'report',
          route: { name: 'order-list' },
        },
        {
          title: 'Pricing',
          icon: 'payment',
          route: { name: 'design-list' },
        },
        // TODO: uncomment me
        // {
        //   title: 'Setting',
        //   icon: 'settings',
        //   route: { name: 'settings' },
        // },
      ]
    },
    availableMenus() {
      return this.menus.filter((menu) => menu.disable !== true)
    },
  },
  data() {
    return {
      hoverIndex: -1,
    }
  },
  methods: {
    isActive(route) {
      if (isObject(route)) {
        return this.$route.name === route.name
      }

      return this.$route.path === route || this.$route.fullPath === route
    },
    onHover(i) {
      this.hoverIndex = i
    },
  },
}
