import mixinCountry from '@/packages/shared/mixins/country'
export default {
  name: 'ModalUpdateOrder',
  mixins: [mixinCountry],
  props: {
    visible: {
      type: Boolean,
      default: false,
    },
    loading: {
      type: Boolean,
      default: false,
    },
    current: {
      type: Object,
      default: () => {},
    },
    countries: {
      type: Array,
      default: () => [],
    },
  },
  computed: {
    countryCode() {
      return this.order.country_code
    },
  },
  data() {
    return {
      order: {
        first_name: null,
        last_name: null,
        full_name: null,
        phone: null,
        address: null,
        address2: null,
        country_code: null,
        state_code: null,
        postcode: null,
        shipping_method: null,
        shipping_method_code: null,
        note: null,
      },
      isShow: this.visible,
    }
  },
  methods: {
    handleClose() {
      this.$emit('update:visible', false)
    },
    async handleSave() {
      const validate = await this.$validator.validateAll()

      if (!validate) {
        return
      }

      this.$emit('save', this.order)
    },
    handleHasProvince(val) {
      if (!val) {
        this.order.state_code = null
      }
    },
  },
  watch: {
    current: {
      handler: function(val) {
        this.order = { ...val }
      },
      deep: true,
      immediate: true,
    },
    visible(value) {
      this.isShow = value
    },
  },
}
